// Main StaffCardGrid Component
const StaffCardGrid = ({ hotelId, className = "" }) => {
  const [staff, setStaff] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // API call to fetch staff data
  const fetchStaff = async () => {
    try {
      setLoading(true);
      setError(null);

      const res = await api.get(`/staff/hotel/${hotelId}`);

      // Handle different response structures
      const staffData = res.data?.content || res.data || [];
      setStaff(staffData);

    } catch (err) {
      console.error('Error fetching staff:', err);
      if (err.response?.status === 404) {
        setError('No staff members found for this hotel.');
      } else {
        setError('Failed to load staff members. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    console.log('StaffCardGrid: hotelId =', hotelId);
    if (hotelId) {
      console.log('StaffCardGrid: Fetching staff for hotelId =', hotelId);
      fetchStaff();
    } else {
      console.log('StaffCardGrid: No hotelId provided, not fetching staff');
      setLoading(false);
    }
  }, [hotelId]);

  if (error) {
    return (
      <div className={`rounded-lg bg-red-50 border border-red-200 p-6 text-center ${className}`}>
        <div className="text-red-600 font-medium mb-2">Error Loading Staff</div>
        <div className="text-red-500 text-sm mb-4">{error}</div>
        <Button 
          variant="outline" 
          onClick={fetchStaff}
          className="border-red-300 text-red-600 hover:bg-red-50"
        >
          Try Again
        </Button>
      </div>
    );
  }

  return (
    <div className={`w-full ${className}`}>
      {/* Header */}
      <div className="flex items-center justify-between mb-6">
        <div className='pl-4'>
          <h2 className="text-lg font-bold text-gray-900">Staff Members</h2>
          <p className="text-sm text-gray-600 mt-1">
            {loading ? 'Loading...' : `${staff.length} staff member${staff.length !== 1 ? 's' : ''} found`}
          </p>
        </div>
      </div>

      {/* Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
        {loading ? (
          // Loading skeletons
          Array.from({ length: 8 }).map((_, index) => (
            <StaffCardSkeleton key={index} />
          ))
        ) : staff.length === 0 ? (
          // Empty state
          <EmptyState />
        ) : (
          // Staff cards
          staff.map((staffMember) => (
            <StaffCard
              key={staffMember.staffId}
              staff={staffMember}
            />
          ))
        )}
      </div>
    </div>
  );
};

export default StaffCardGrid;

