          <ActionButton
            key={action}
            action={action}
            disabled={isDisabled}
            onClick={() => {
              if (action === "view") onViewDetails(booking);
              else if (action === "directions") onDirections(booking);
              else if (action === "contact") onContact(booking);
              else if (action === "extend") onExtend(booking);
              else if (action === "cancel") onCancel(booking);
            }}
          />
        ))}
      </div>
    </div>
  );
};

