import React, { useState, useEffect } from "react";
import {
  LineChart,
  Line,
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
  Legend,
} from "recharts";
import {
  Calendar,
  TrendingUp,
  BarChart3,
  LineChart as LineChartIcon,
  Download,
} from "lucide-react";
import * as XLSX from 'xlsx';
import { Card, CardContent, CardHeader, CardTitle } from "@/shared/components/card";
import api from "../../shared/services/Api";
import { useAuth } from "@/features/authentication";
import YakRoomsLoader from "@/shared/components/YakRoomsLoader";

const BookingsTrendChart = () => {
  const { hotelId } = useAuth();
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [chartType, setChartType] = useState("bar"); // 'bar' or 'line'
  const [selectedDate, setSelectedDate] = useState("2025-01-01");

  // Format month for display
  const formatMonthLabel = (monthString) => {
    const date = new Date(monthString + "-01");
    return date.toLocaleDateString("en-US", {
      month: "short",
      year: "2-digit",
    });
  };

  // Export data to Excel
  const exportToExcel = () => {
    if (!data || data.length === 0) {
      alert("No data available to export");
      return;
    }

    try {
      // Prepare data for Excel export
      const exportData = data.map((item, index) => ({
        'Month': item.monthLabel,
        'Month-Year': item.month,
        'Total Bookings': item.bookings,
        'Rank': index + 1
      }));

      // Add summary statistics
      const totalBookings = data.reduce((sum, item) => sum + item.bookings, 0);
      const averageBookings = Math.round(totalBookings / data.length);
      const peakBookings = Math.max(...data.map((item) => item.bookings));
      const peakMonth = data.find(item => item.bookings === peakBookings)?.monthLabel || 'N/A';

      // Add empty row and summary
      exportData.push({});
      exportData.push({
        'Month': 'SUMMARY STATISTICS',
        'Month-Year': '',
        'Total Bookings': '',
        'Rank': ''
      });
      exportData.push({
        'Month': 'Total Bookings (12 months)',
        'Month-Year': '',
        'Total Bookings': totalBookings,
        'Rank': ''
      });
      exportData.push({
        'Month': 'Average per Month',
        'Month-Year': '',
        'Total Bookings': averageBookings,
        'Rank': ''
      });
      exportData.push({
        'Month': 'Peak Month',
        'Month-Year': peakMonth,
        'Total Bookings': peakBookings,
        'Rank': ''
      });

      // Create workbook and worksheet
      const wb = XLSX.utils.book_new();
      const ws = XLSX.utils.json_to_sheet(exportData);

      // Set column widths for better formatting
      const colWidths = [
        { wch: 15 }, // Month
        { wch: 12 }, // Month-Year
        { wch: 15 }, // Total Bookings
        { wch: 8 }   // Rank
      ];
      ws['!cols'] = colWidths;

      // Add worksheet to workbook
      XLSX.utils.book_append_sheet(wb, ws, "Booking Trends");

      // Generate filename with current date
      const currentDate = new Date().toISOString().split('T')[0];
      const filename = `booking-trends-report-${currentDate}.xlsx`;

      // Save the file
      XLSX.writeFile(wb, filename);

      console.log(`Excel file exported successfully: ${filename}`);
    } catch (error) {
      console.error("Error exporting to Excel:", error);
      alert("Failed to export data. Please try again.");
    }
  };

  // Fetch data from API
  const fetchBookingsData = async () => {
    if (!hotelId) {
      setLoading(false);
      return;
    }

    try {
      setLoading(true);
      setError(null);

      const res = await api.get(
        `/booking-statistics/monthly/hotel/${hotelId}?startDate=${selectedDate}`
      );

      console.log("Fetching from:", res.config.url);

      const result = res.data;

      // Validate response structure
      if (!Array.isArray(result)) {
        throw new Error(
          "Invalid API response: Expected an array of booking data"
        );
      }

      // Process the data to add formatted month labels
      const processedData = result
        .map((item) => {
          if (!item.monthYear || typeof item.bookingCount !== "number") {
            console.warn("Invalid data item:", item);
            return null;
          }
          return {
            month: item.monthYear,
            bookings: item.bookingCount,
            monthLabel: formatMonthLabel(item.monthYear),
          };
        })
        .filter(Boolean);

      if (processedData.length === 0) {
        throw new Error("No valid booking data received from API");
      }

      setData(processedData);
      setLoading(false);
    } catch (err) {
      console.error("Error fetching bookings data:", err);
      setError(err.message);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchBookingsData();
  }, [selectedDate, hotelId]);

  // Custom tooltip component
  const CustomTooltip = ({ active, payload, label }) => {
    if (active && payload && payload.length) {
      return (
        <div className="bg-white p-3 border border-gray-200 rounded-lg shadow-lg">
          <p className="font-medium text-gray-900">{label}</p>
          {payload.map((entry, index) => (
            <p key={index} className="text-sm" style={{ color: entry.color }}>
              {entry.name}:{" "}
              <span className="font-semibold">{entry.value} bookings</span>
            </p>
          ))}
        </div>
      );
    }
    return null;
  };

  // Loading skeleton
  if (loading) {
    return (
      <Card className="w-full">
        <CardHeader className="pb-4">
          <CardTitle className="text-xl font-semibold text-gray-900 flex items-center gap-2">
            <TrendingUp className="h-5 w-5 text-blue-600" />
            Monthly Booking Trends
          </CardTitle>
        </CardHeader>
        <CardContent className="p-0">
          <div className="flex items-center justify-center h-96">
            <div className="text-center">
              <YakRoomsLoader 
                size={112} 
                showTagline={false} 
                loadingText=""
                className="mb-4"
              />
              <p className="text-gray-600">Loading booking data...</p>
            </div>
          </div>
        </CardContent>
      </Card>
    );
  }

  // Error state
  if (error) {
    return (
      <Card className="w-full">
        <CardHeader className="pb-4">
          <CardTitle className="text-xl font-semibold text-gray-900 flex items-center gap-2">
            <TrendingUp className="h-5 w-5 text-blue-600" />
            Monthly Booking Trends
          </CardTitle>
        </CardHeader>
        <CardContent className="p-0">
          <div className="text-center py-8">
            <p className="text-red-600 mb-4">
              Error loading booking data: {error}
            </p>
            <button
              onClick={fetchBookingsData}
              className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
            >
              Retry
            </button>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className="w-full">
      <CardHeader className="pb-4">
        <CardTitle className="text-xl font-semibold text-gray-900 flex items-center gap-2">
          <TrendingUp className="h-5 w-5 text-blue-600" />
          Monthly Booking Trends
        </CardTitle>
        <p className="text-sm text-gray-500 mt-1">
          Track monthly booking patterns and trends
        </p>

        {/* Controls */}
        <div className="flex flex-wrap items-center justify-between gap-4 mt-4">
          <div className="flex flex-wrap items-center gap-4">
            {/* Chart Type Toggle */}
            <div className="flex items-center gap-2">
              <button
                className={`px-3 py-2 text-sm rounded-md transition-colors ${
                  chartType === "bar"
                    ? "bg-blue-600 text-white"
                    : "bg-gray-100 text-gray-700 hover:bg-gray-200"
                }`}
                onClick={() => setChartType("bar")}
              >
                <BarChart3 className="h-4 w-4 inline mr-1" />
                Bar
              </button>
              <button
                className={`px-3 py-2 text-sm rounded-md transition-colors ${
                  chartType === "line"
                    ? "bg-blue-600 text-white"
                    : "bg-gray-100 text-gray-700 hover:bg-gray-200"
                }`}
                onClick={() => setChartType("line")}
              >
                <LineChartIcon className="h-4 w-4 inline mr-1" />
                Line
              </button>
            </div>

            {/* Date Picker */}
            <div className="flex items-center gap-2">
              <Calendar className="h-4 w-4 text-gray-500" />
              <input
                type="date"
                value={selectedDate}
                onChange={(e) => setSelectedDate(e.target.value)}
                min="2025-01-01"
                className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm"
              />
            </div>
          </div>

          {/* Export Button */}
          <button
            onClick={exportToExcel}
            disabled={!data || data.length === 0}
            className={`flex items-center gap-2 px-4 py-2 text-sm font-medium rounded-md transition-colors ${
              data && data.length > 0
                ? "bg-gray-100 hover:bg-gray-200 text-gray-700 border border-gray-300"
                : "bg-gray-300 text-gray-500 cursor-not-allowed"
            }`}
            title={data && data.length > 0 ? "Export data to Excel" : "No data available to export"}
          >
            <Download className="h-4 w-4" />
            Export Excel
          </button>
        </div>
      </CardHeader>

      <CardContent className="p-0">
        <div className="w-full h-80 md:h-96 lg:h-[500px]">
          <ResponsiveContainer width="100%" height="100%">
            {chartType === "bar" ? (
              <BarChart
                data={data}
                margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
              >
                <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" />
                <XAxis
                  dataKey="monthLabel"
                  tick={{ fontSize: 14, fontWeight: 500 }}
                  tickLine={{ stroke: "#e0e0e0" }}
                  axisLine={{ stroke: "#e0e0e0", strokeWidth: 1 }}
                />
                <YAxis
                  tick={{ fontSize: 14, fontWeight: 500 }}
                  tickLine={{ stroke: "#e0e0e0" }}
                  axisLine={{ stroke: "#e0e0e0", strokeWidth: 1 }}
                />
                <Tooltip content={<CustomTooltip />} />
                <Legend />
                <Bar
                  dataKey="bookings"
                  name="This Year"
                  fill="#3b82f6"
                  radius={[1, 1, 0, 0]}
                />
              </BarChart>
            ) : (
              <LineChart
                data={data}
                margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
              >
                <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" />
                <XAxis
                  dataKey="monthLabel"
                  tick={{ fontSize: 14, fontWeight: 500 }}
                  tickLine={{ stroke: "#e0e0e0" }}
                  axisLine={{ stroke: "#e0e0e0", strokeWidth: 1 }}
                />
                <YAxis
                  tick={{ fontSize: 14, fontWeight: 500 }}
                  tickLine={{ stroke: "#e0e0e0" }}
                  axisLine={{ stroke: "#e0e0e0", strokeWidth: 1 }}
                />
                <Tooltip content={<CustomTooltip />} />
                <Legend />
                <Line
