// Utility function to format time from 24-hour to 12-hour format with descriptive text
const formatTimeWithDescription = (timeString, type = 'general') => {
  if (!timeString) return "Not specified";

  try {
    const [hours, minutes] = timeString.split(':').map(Number);
    const hour12 = hours === 0 ? 12 : hours > 12 ? hours - 12 : hours;
    const ampm = hours >= 12 ? 'PM' : 'AM';
    const formattedMinutes = minutes.toString().padStart(2, '0');

    let description = '';
    if (hours === 0) {
      description = '(Midnight)';
    } else if (hours >= 1 && hours < 6) {
      description = type === 'checkin' ? '(Evening)' : '(Morning)';
    } else if (hours >= 6 && hours < 12) {
      description = '(Morning)';
    } else if (hours === 12) {
      description = '(Noon)';
    } else if (hours >= 13 && hours < 17) {
      description = '(Afternoon)';
    } else if (hours >= 17 && hours < 21) {
      description = type === 'checkout' ? '(Evening)' : '(Evening)';
    } else {
      description = '(Night)';
    }

    return `${hour12}:${formattedMinutes} ${ampm} ${description}`;
  } catch (error) {
    console.error('Error formatting time:', error);
    return "Invalid time";
  }
};

// Simple time formatter for sidebar (without description)
