                    <InfoItem
                      icon={Calendar}
                      label="Check-out"
                      value={formatDate(bookingDetails.checkOutDate)}
                      className="sm:pl-4"
                    />
                  </div>
                </div>
              </div>

              <Separator />

              {/* Guest Information */}
              <div className="space-y-4">
                <h3 className="text-base font-semibold text-foreground flex items-center gap-2">
                  <User className="h-4 w-4" />
                  Guest Information
