      duration: 1000, // animation duration
      once: true, // only animate once
    });
  }, []);

  useEffect(() => {
    const timer = setTimeout(() => {
      setRating(true);
    }, 3 * 60 * 1000);

    return () => clearTimeout(timer);
  }, []);

  useEffect(() => {
    if (!dismissed) return;

    const interval = setInterval(() => {
      setRating(true);
    }, 10 * 60 * 1000);

    return () => clearInterval(interval);
  }, [dismissed]);

  const handleDismiss = () => {
